    my_grid &lt;- my_grid[!(my_grid$u %in% cur_files), ]

    for (i in my_grid$u) {
      print(paste0(base_u, i))
      w &lt;- read_html(paste0(base_u, i))

      links &lt;- w |&gt; html_nodes("a") |&gt; html_attr("href")

      my_link &lt;- links[grep(
        links,
        pattern = "gp-reg-pat-prac-sing-age-(fe)?male"
      )]

      for (tlink in my_link) {
        j &lt;- str_extract(tlink, pattern = "(fe)?male")

        download.file(
          url = tlink,
          destfile = paste0(
            folder_path,
            "/",
            i,
            "-",
            j,
            ".",
            tools::file_ext(tlink)
          ),
          mode = "wb"
        )
      }

      Sys.sleep(rnorm(1, mean = 5))
    }

    # List all ZIP files in the folder
    zip_files &lt;- list.files(
      path = folder_path,
      pattern = "\\.zip$",
      full.names = TRUE
    )

    # Extract each ZIP file
    lapply(zip_files, function(zip_file) {
      # Create a temporary directory for extraction
      temp_dir &lt;- tempfile()
      dir.create(temp_dir)

      # Extract the ZIP file into the temporary directory
      unzip(zip_file, exdir = temp_dir)

      # List extracted files
      extracted_files &lt;- list.files(path = temp_dir, full.names = TRUE)

      # Move and rename each extracted file to the original folder
      lapply(extracted_files, function(file) {
        file_extension &lt;- tools::file_ext(file)
        new_name &lt;- file.path(
          folder_path,
          paste0(
            tools::file_path_sans_ext(basename(zip_file)),
            ".",
            file_extension
          )
        )
        file.rename(file, new_name)
      })

      # Remove the temporary directory
      unlink(temp_dir, recursive = TRUE)
    })

    all_data &lt;- lapply(
      list.files(
        path = "gp_patients_age",
        pattern = "\\.csv$",
        full.names = T
      ),
      \(x) {
        read_csv(x) |&gt;
          mutate(
            month = str_extract(basename(x), "[a-zA-Z]+(?=-)"),
            year = str_extract(basename(x), "\\d{4}")
          )
      }
    )

    common_names &lt;- reduce(lapply(all_data, names), intersect)

    all_data_df_age &lt;- do.call(
      rbind,
      lapply(all_data, \(x) {
        x[, common_names]
      })
    ) |&gt;
      mutate(year = as.integer(year))

    all_data_df_age = data.table::as.data.table(all_data_df_age)

    all_data_df_age[,
      AGE_GROUP := cut(
        as.numeric(AGE),
        breaks = seq(0, 120, 10),
        include.lowest = TRUE
      )
    ]

    all_data_df_age_summary &lt;- all_data_df_age[
      !is.na(AGE_GROUP),
      .(NUMBER_OF_PATIENTS = sum(NUMBER_OF_PATIENTS)),
      by = .(ORG_CODE, AGE_GROUP, month, year)
    ]

    write_csv(
      all_data_df_age_summary,
      file = file.path(here(), "data_raw", "practice_patients_age.csv")
    )
  }

  get_patients()
} else {
  all_data_df_age &lt;- read_csv(
    file.path(here(), "data_raw", "practice_patients_age.csv"),
    col_types = cols(
      ORG_CODE = col_character(),
      AGE_GROUP = col_character(),
      month = col_character(),
      year = col_double(),
      NUMBER_OF_PATIENTS = col_double()
    )
  )
}






